from selenium import webdriver
from time import sleep
import csv
import string

#open csv file to write to, construct the writer function and label the rows
csv_file=open('al_jazeera_final.csv', 'w', encoding='utf-8', newline='')
writer=csv.writer(csv_file)
writer.writerow(['title', 'header', 'caption', 'category', 'authors', 'datetime', 'article'])

#declare driver and point to domain
driver = webdriver.Chrome()
driver.get("https://www.aljazeera.com")

#click button to get to opinions column in window
button_url = driver.find_element_by_xpath('//li[@data-guid="181119115524858"]').find_element_by_tag_name('a').get_attribute('href')
driver.get(button_url)

#this declares the show more button, scrolls to bottom of the page and clicks it 
#repeatedly for numrange number of times

#this value was chosen because the opinions column only goes this far
#a method which auto-corrects will be introduced in the future
numrange=99
i=0
while i < numrange:

    #the try except is built to handle cases of intermittent bandwidth
    button_found = False
    try:
        driver.execute_script("window.scrollTo(0, document.body.scrollHeight);")
        button = driver.find_element_by_id("btn_showmore_b1_4113")
        button.click()
        sleep(2)
        driver.execute_script("return document.documentElement.outerHTML")
        sleep(2)
        print(i)
        button_found = True
        i = i+1
    except:
        continue

    #this reduces the index by one when button is not clicked
    if not button_found:
        print("passed button, try again")
        print(i)
        i=i-1
        sleep(1)


#this declares the row elements for all articles in the opinions column
rows = driver.find_elements_by_tag_name('h2')[0:2] + [driver.find_element_by_xpath('//div[@class="opinion-topMainSec-rt-top opinion-topmain-card"]')] + [driver.find_element_by_xpath('//div[@class="opinion-topMainSec-rt-bottom opinion-topmain-card"]')] + driver.find_elements_by_tag_name('h2')[2:]
print("compiling urls")

#iteratively pulls urls from each row element and appends them to page_urls
#better to be designed as a dictionary key value assignment subsequently
page_urls=[]
for row in rows:
     page_url = row.find_element_by_tag_name('a').get_attribute('href')
     page_urls.append(page_url)

